    cin >> val_ini;
    maior_total_collatz(val_ini, val_maior, quant);
    cout << "Maior valor: " << val_maior << endl;
    cout << "Total de termos: " << quant << endl;
    return 0;
}
void maior_total_collatz(int t_ini, int& maior,
                         int& total){
    int termo = maior = t_ini;
    total = 1;
    while(termo != 1){
        if(termo % 2 == 0){
            termo = termo/2;
        }
        else{
            termo = 3*termo + 1;
